     "cell_type": "code",
     "collapsed": false,
     "input": [
      "import numpy as np\n",
      "\n",
      "def circle(R, x0, y0, n=501):\n",
      "    t = np.linspace(0, 1, n)\n",
      "    x = x0 + R*np.cos(2*np.pi*t)\n",
      "    y = y0 + R*np.sin(2*np.pi*t)\n",
      "    return x, y\n",
      "\n",
